const initialstate = {
  searchfield: "",
};
export const searchfieldReducer = (state = initialstate, action = {}) => {
  switch (action.type) {
    case CHANGE_SEARCH_FIELD:
      return { ...state, searchfield: action.payload };
    default:
      return state;
  }
};
